import { createContext, ReactNode, useState } from "react";
import API from "../dataLayer/api";

type User = {
    email: string;
    id: string;
    pass: string;
    username: string;
}

type GlobalState = {
    loggedIn: boolean;
    user?: User;
}

type ProviderState = {
    state: GlobalState;
    onLogin?: (username: string, password: string) => any;
    onLogout?: () => void;
}

type Props = {
    children: ReactNode;
}
export const GlobalContext = createContext<ProviderState>({ state: { loggedIn: false } });

export function GlobalContextProvider({ children }: Props)
{
    const [state, setState] = useState<GlobalState>({ loggedIn: false })
    const [loading, setLoading] = useState<boolean>(false);

    const handleLogIn = async (username: string, password: string) =>
    {
        const result = await API.login(username, password);
        setState({ loggedIn: true })
    }

    const handleLogOut = () =>
    {
        setState({ loggedIn: false })
    }

    const ProviderObj = {
        state,
        onLogin: handleLogIn,
        onLogout: handleLogOut
    }

    return (
        <GlobalContext.Provider value={ProviderObj}>
            {children}
            { }
        </GlobalContext.Provider>)
}
