import { useState } from "react";

export function useForm<T>(defaultValues: T, onSuccess: (values: T) => void) {
    const [formValues, setFormValues] = useState<T>(defaultValues);
    return {
        formProps: {
            onSubmit: () => {
                onSuccess(formValues);
            }
            
        }
    }
}
