import { useState } from "react";
import API from "../../dataLayer/api"
import Login, { LoginFormValues } from "../../components/public/login";
import { Container } from "semantic-ui-react";
import { GlobalContext, GlobalContextProvider } from "../../contexts/globalContext";

export function LoginView()
{
    const [loading, setLoading] = useState<boolean>(false);
    return (
        <GlobalContextProvider>
            <GlobalContext.Consumer>
                {value =>
                    value.onLogin ? <Container>
                        <Login onSuccess={value.onLogin} loading={loading} />
                    </Container> : <Container>
                        <Login onSuccess={() => { console.log("Something went wrong.") }} loading={loading} />
                    </Container>
                }
            </GlobalContext.Consumer>
        </GlobalContextProvider>
    )
}
