import { Button, Container, Form } from "semantic-ui-react";
// import { useForm } from "../../../hooks/useForm";
import { useForm } from "react-hook-form";

export type SignUpFormValues = {
    username: string;
    email: string,
    firstname: string,
    lastname: string,
    password: string,
    confirmPassword: string;
}

type LoginProps = {
    onSuccess: (formValues: SignUpFormValues) => void;
    onError?: (errors: any) => void;
}

export default function SignUp({ onSuccess, onError }: LoginProps) {

    const { handleSubmit, register } = useForm<SignUpFormValues>();

    return (
        <Form onSubmit={handleSubmit(onSuccess)}>
            <Form.Field>
                <label>Username</label>
                <input {...register("username")} name="username" placeholder="Username" />
            </Form.Field>
            <Form.Field>
                <label>Email</label>
                <input {...register("email")} name="email" placeholder="Email" />
            </Form.Field>
            <Form.Field>
                <label>First name</label>
                <input {...register("firstname")} name="firstname" placeholder="Username" />
            </Form.Field>
            <Form.Field>
                <label>Username</label>
                <input {...register("username")} name="username" placeholder="Username" />
            </Form.Field>
            <Form.Field>
                <label>Password</label>
                <input {...register("password")} type="password" name="password" placeholder="Password" />
            </Form.Field>
            <Form.Field>
                <label>Confirm Password</label>
                <input {...register("confirmPassword")} type="password" name="confirmPassword" placeholder="Confirm Password" />
            </Form.Field>
            <Button type="submit">
                Log In
            </Button>
        </Form>
    );
}
