import { Button, Form } from "semantic-ui-react";
import { useForm } from "react-hook-form";
import { GlobalContext, GlobalContextProvider } from "../../../contexts/globalContext";

export type SignUpFormValues = {
    username: string;
    email: string,
    password: string,
    confirmPassword: string;
}

type LoginProps = {
    onSuccess: (formValues: SignUpFormValues) => void;
    onError?: (errors: any) => void;
    loading?: boolean;
}

export default function SignUp({ onSuccess, onError, loading }: LoginProps)
{

    const { handleSubmit, register } = useForm<SignUpFormValues>();



    return (
        <GlobalContextProvider>
            <GlobalContext.Consumer>
                
                {value => <p>{value.state.loggedIn ? "Logged in" : "Not Logged in"}</p>}
            </GlobalContext.Consumer>
        </GlobalContextProvider>
    );
}
