import { Button, Container, Form } from "semantic-ui-react";
import { useForm } from "../../../hooks/useForm";

export type signUpFormValues = {
    username?: string;
    password?: string;
}

type LoginProps = {
    onSuccess: (formValues: signUpFormValues) => void;
    onError?: (errors: any) => void;
}

export default function SignUp({ onSuccess, onError }: LoginProps) {
    const { formProps, getFieldProps } = useForm<signUpFormValues>({}, onSuccess);

    return (
        <Form {...formProps}>
            <Form.Field>
                <label>Username</label>
                <input {...getFieldProps("username")} name="username" placeholder="Username" />
            </Form.Field>
            <Form.Field>
                <label>Email</label>
                <input {...getFieldProps("email")} name="email" placeholder="Email" />
            </Form.Field>
            <Form.Field>
                <label>Password</label>
                <input {...getFieldProps("password")} type="password" name="password" placeholder="Password" />
            </Form.Field>
            <Button type="submit">
                Log In
            </Button>
        </Form>
    );
}
