import { SignUpFormValues } from "../components/public/signUp";
import { v4 as uuidv4 } from 'uuid';

type APIResponse = {
    data?: any,
    status: string;
}

function checkPassword(pass: string, confirm: string) {
    console.log(!pass.includes(" "));
    console.log()
    if (!pass.includes(" ") && pass.match(/0-9/ig) && pass.match(/a-z/ig) && pass.length > 7 && pass === confirm) {
        return true;
    }
    return false
}

export default class API {
    static signUp(formValues: SignUpFormValues): Promise<APIResponse> {
        const userID = uuidv4();
        const { password, confirmPassword, ...user } = formValues;
        console.log(checkPassword(password, confirmPassword))
        if (user.username && user.email && password && checkPassword(password, confirmPassword)) {
            console.log(user.username, user.email)
            window.localStorage.setItem(userID, JSON.stringify(user));
            return new Promise((resolve: Function, reject: Function) => {
                setTimeout(() => {
                    resolve({
                        data: user,
                        status: "success"
                    });
                }, Math.random() * 500 + 1000)
            });
        }
        return new Promise((resolve: Function, reject: Function) => {
            setTimeout(() => {
                resolve({
                    data: "Incorrect values",
                    status: "error"
                });
            }, Math.random() * 500 + 1000)
        });
    }
    static login(username: string, password: string) {
        return new Promise((resolve: Function, reject: Function) => {

            const hashedPassword = btoa(username + password);

            const userJSON = window.localStorage.getItem(hashedPassword);

            if (userJSON) {
                const user = JSON.parse(userJSON);

                setTimeout(() => {
                    resolve({
                        data: user,
                        status: "success"
                    });
                }, Math.random() * 500 + 1000)
            }

            setTimeout(() => {
                resolve({
                    data:
                        { message: "Could not find user" },
                    status: "error"
                });
            }, Math.random() * 500 + 1000)
        });
    }
}
