import { SignUpFormValues } from "../components/public/signUp";

export default class API {
    static signUp(formValues: SignUpFormValues) {
        const { password, confirmPassword, ...user } = formValues;
        const hashedPassword = btoa(password);
        window.localStorage.setItem(hashedPassword, JSON.stringify(user));
        return new Promise((resolve: Function, reject: Function) => {
            setTimeout(() => {
                resolve({
                    data:
                    {
                        data: user,
                        firstname: "Mike",
                    },
                    status: "success"
                });
            }, Math.random() * 500 + 1000)
        });
    }
    static login(username: string, password: string) {
        return new Promise((resolve: Function, reject: Function) => {
            setTimeout(() => {
                resolve({
                    data:
                    {
                        firstname: "Mike",
                    },
                    status: "success"
                });
            }, Math.random() * 500 + 1000)
        });
    }
}
