import { useState } from "react";
import API from "../../dataLayer/api"
import { Container, Message } from "semantic-ui-react";
import SignUp, { SignUpFormValues } from "../../components/public/signUp";
import { useNavigate } from "react-router";

export function SignUpView() {
    const [loading, setLoading] = useState<boolean>(false);
    const [success, setSuccess] = useState<boolean>(false);
    const [redirect, setRedirect] = useState<boolean>(false);
    const navigate = useNavigate();

    const handleSignUp = async (values: SignUpFormValues) => {

        setLoading(true);

        const { data, status } = await API.signUp(values);

        if (status === "success") {
            setSuccess(true);
            setRedirect(true);
        }

        setLoading(false);
    }
    return (
        <Container style={{ width: 400, margin: 48 }}>
            {loading ? <span>Signing up...</span> : null}
            {
                success ? <Message success>Thank you for signing up!</Message> : null
            }
            <SignUp onSuccess={handleSignUp} />
        </Container>
    )
}
