import { useState } from "react";
import API from "../../dataLayer/api"
import Login, { LoginFormValues } from "../../components/public/login";
import { Container } from "semantic-ui-react";
import { GlobalContext, GlobalContextProvider } from "../../contexts/globalContext";

export function LoginView()
{
    const [loading, setLoading] = useState<boolean>(false);
    const handleSignIn = async (formValues: LoginFormValues) =>
    {
        const { username, password } = formValues;

        if (!username || !password)
        {
            return
        }

        setLoading(true);

        try
        {
            const result = await API.login(username, password);
            if (result.status == "success")
            {
                //SUCCESS LOGIN
            }
        }
        catch (e)
        {

        }
        finally
        {
            setLoading(false);
        }
    }
    return (
        <GlobalContextProvider>
            <GlobalContext.Consumer>
                {value =>
                    value.onLogin ? <Container>
                        <Login onSuccess={value.onLogin} loading={loading} />
                    </Container> : <Container>
                        <Login onSuccess={() => { console.log("This isn't working") }} loading={loading} />
                    </Container>
                }
            </GlobalContext.Consumer>
        </GlobalContextProvider>
    )
}
