import React, { useContext } from 'react';
import { Route, Routes } from 'react-router';
import { Link } from 'react-router-dom';
import { Button, ButtonGroup, Dropdown, Menu } from 'semantic-ui-react';
import './App.css';
import { GlobalContext } from './contexts/globalContext';
import { FileListView } from './views/protected/fileList.view';
import { HomeView } from './views/public/home.view';
import { LoginView } from './views/public/login.view';
import { NotFound404 } from './views/public/NotFound404';
import { SignUpView } from './views/public/signUp.view';

function PublicRoutes()
{
    return (
        <>
            <header>
                <Menu>
                    <Menu.Item>
                        <Link to="/"><h2>VEdit</h2></Link>
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <Link to="/signup">Sign Up</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/login">Log In</Link>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
            </header>
            <Routes>
                <Route path="/" element={<HomeView />} />
                <Route path="/login" element={<LoginView />} />
                <Route path="/signup" element={<SignUpView />} />
                <Route path="/company/:id?" element={<SignUpView />} />
                <Route path="*" element={<NotFound404 />} />
            </Routes>
        </>
    )
}

function ProtectedRoutes({ user, logOut }: any)
{
    console.log(user.pfp);
    return (
        <>
            <header>
                <Menu>
                    <Menu.Item>
                        <Link to="/"><h2>VEdit</h2></Link>
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <p>Welcome, {user.username}!</p>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/projects">Projects</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/account"><ButtonGroup><Button>Account</Button><Button><Dropdown icon={user.pfp}>
                                <Dropdown.Menu>
                                    <Dropdown.Item>
                                        Test
                                    </Dropdown.Item>

                                </Dropdown.Menu>
                            </Dropdown></Button></ButtonGroup><img className='smallpfp' src={user.pfp} alt="User's profile" /></Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link onClick={logOut} to="/">Log Out</Link>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
            </header>
            <Routes>
                <Route path="/" element={<HomeView />} />
                <Route path="/login" element={<LoginView />} />
                <Route path="/signup" element={<SignUpView />} />
                <Route path="/projects" element={<FileListView />} />
                <Route path="/company/:id?" element={<SignUpView />} />
                <Route path="*" element={<NotFound404 />} />
            </Routes>
        </>
    )
}

function App()
{
    const { state: { loggedIn, user }, onLogout } = useContext(GlobalContext);
    return (
        <>
            {loggedIn ? <ProtectedRoutes user={user} logOut={onLogout} /> : <PublicRoutes />}
        </>
    );
}

export default App;
