import { Button, Form } from "semantic-ui-react";
import { useForm } from "react-hook-form";
import { GlobalContext, GlobalContextProvider } from "../../../contexts/globalContext";

export type LoginFormValues = {
    username?: string;
    password?: string;
}

type LoginProps = {
    onSuccess: (formValues: LoginFormValues) => void;
    onError?: (errors: any) => void;
    loading?: boolean;
}

export default function Login({ onSuccess, onError, loading }: LoginProps)
{
    const { handleSubmit, register } = useForm<LoginFormValues>();

    return (
        <GlobalContextProvider>
            <GlobalContext.Consumer>
                {value =>
                    <Form onSubmit={handleSubmit(onSuccess), handleSubmit}>
                        <Form.Field>
                            <label>Username/Email</label>
                            <input {...register("username")} name="username" placeholder="Username/Email" />
                        </Form.Field>
                        <Form.Field>
                            <label>Password</label>
                            <input {...register("password")} type="password" name="password" placeholder="Password" />
                        </Form.Field>
                        <Button type="submit">
                            {loading ? "Loading..." : "Log In"}
                        </Button>
                    </Form>
                }
            </GlobalContext.Consumer>
        </GlobalContextProvider>
    );
}
