import { useState } from "react";
import { Accordion, Icon } from "semantic-ui-react";

export default function FileList()
{
    const [state, setState] = useState<number>(-1);

    const handleClick = (e: any, titleProps: any) =>
    {
        const { index } = titleProps
        const activeIndex = state
        const newIndex = activeIndex === index ? -1 : index

        setState(newIndex)
    }

    const user = useContext(GlobalContext).state.user;
    const { handleSubmit, register } = useForm();
    return (
        <Accordion>
            <Accordion.Title
                active={state === 0}
                index={0}
                onClick={handleClick}
            >
                <Icon name='dropdown' />
                Change Username
            </Accordion.Title>
            <Accordion.Content active={state === 0}>
                <Form onSubmit={handleSubmit(projectHandler)}>
                    <Form.Field>
                        <label>New Username</label>
                        <input {...register("username")} name="username" placeholder="New Username" />
                    </Form.Field>
                    <Form.Field>
                        <label>Confirm New Username</label>
                        <input {...register("confirmusername")} name="confirmusername" placeholder="Confirm New Username" />
                    </Form.Field>
                    <Button type="submit">
                        Change Username
                    </Button>
                </Form>
            </Accordion.Content>
        </Accordion>
    );
}
