import React, { useContext } from 'react';
import { Route, Routes } from 'react-router';
import { Link } from 'react-router-dom';
import { Menu } from 'semantic-ui-react';
import './App.css';
import { GlobalContext, GlobalContextProvider } from './contexts/globalContext';
import { FileListView } from './views/protected/fileList.view';
import { LoginView } from './views/public/login.view';
import { NotFound404 } from './views/public/NotFound404';
import { SignUpView } from './views/public/signUp.view';

function PublicRoutes()
{
    return (
        <div>
            <header>
                <Menu>
                    <Menu.Item>
                        <Link to="/"><h2>VEdit</h2></Link>
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <Link to="/projects">Projects</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/signup">Sign Up</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/login">Log In</Link>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
            </header>
            <Routes>
                <Route path="/" element={<LoginView />} />
                <Route path="/login" element={<LoginView />} />
                <Route path="/signup" element={<SignUpView />} />
                <Route path="/projects" element={<FileListView />} />
                <Route path="/company/:id?" element={<SignUpView />} />
                <Route path="*" element={<NotFound404 />} />
            </Routes>
        </div>
    )
}

function ProtectedRoutes({User: any})
{
    return (
        <div>
            <header>
                <Menu>
                    <Menu.Item>
                        <Link to="/"><h2>VEdit</h2></Link>
                    </Menu.Item>
                    <Menu.Menu position='right'>
                        <Menu.Item>
                            <Link to="/projects">Projects</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/projects">Welcome, {User.username}!</Link>
                        </Menu.Item>
                        <Menu.Item>
                            <Link to="/">Log Out</Link>
                        </Menu.Item>
                    </Menu.Menu>
                </Menu>
            </header>
            <Routes>
                <Route path="/" element={<LoginView />} />
                <Route path="/login" element={<LoginView />} />
                <Route path="/signup" element={<SignUpView />} />
                <Route path="/projects" element={<FileListView />} />
                <Route path="/company/:id?" element={<SignUpView />} />
                <Route path="*" element={<NotFound404 />} />
            </Routes>
        </div>
    )
}

function App()
{
    const { state: { loggedIn, user } } = useContext(GlobalContext);
    console.log(loggedIn);
    return (
        <GlobalContextProvider>
            <div>{loggedIn ? <ProtectedRoutes User={user} /> : <PublicRoutes />}<p>{`${loggedIn}`}</p></div>
        </GlobalContextProvider>
    );
}

export default App;
