import { useState } from "react";
import { Button, Container, Form } from "semantic-ui-react";
import { useForm } from "../../../hooks/useForm";

export type LoginFormValues = {
    username?: string;
    password?: string;
}

type LoginProps = {
    onSuccess: (formValues: LoginFormValues) => void;
    onError?: (errors: any) => void;
}

export default function Login({ onSuccess, onError }: LoginProps) {
    const [formValues, setFormValues] = useState<LoginFormValues>({});

    const { formProps, getFieldProps } = useForm<LoginFormValues>({}, onSuccess);

    const handleSubmit = () => {
        onSuccess(formValues);
    }

    const handleChange = ({ target: { value, name } }: any) => {
        setFormValues({ ...formValues, [name]: value })
    }

    return (
        <Form {...formProps}>
            <Form.Field>
                <label>Username/Email</label>
                <input {...get} name="username" placeholder="Username/Email" />
            </Form.Field>
            <Form.Field>
                <label>Password</label>
                <input {...getFieldProps} name="password" placeholder="Password" />
            </Form.Field>
            <Button type="submit">
                Log In
            </Button>
        </Form>
    );
}
